import React, { useEffect, useState, useContext } from "react";
import axios from "axios";
import Products from "./Products";
import Options from './Options'
import ErrorBanner from "../../components/ErrorBanner"
import OrderContext from '../../contexts/OrderContext'

const Type = ({ orderType }) => {
  const [items, setItems] = useState([]);
  const [error, setError] = useState(false);

  const [orderDatas, updateItemCount] = useContext(OrderContext);

  useEffect(() => {
    loadItems(orderType);
  }, [orderType]);

  const loadItems = async (orderType) => {
    try {
      const response = await axios.get(`http://localhost:5000/${orderType}`);
      setItems(response.data);
    } catch (error) {
      setError(true);
    }
  };

  // 에러 발생시
  if (error) {
    return <ErrorBanner message="에러가 발생했습니다." />
  }

  const ItemComponents = orderType === "products" ? Products : Options;

  const optionItems = items.map((item) => (
    <ItemComponents
      key={item.name}
      name={item.name}
      imagePath={item.imagePath}
      updateItemCount={(itemName, newItemCount) => updateItemCount(itemName, newItemCount, orderType)}
    />
  ));

  return (
    <React.Fragment>
      <h2>주문 종류</h2>
      <p>하나의 가격</p>
      <p>총 가격 :</p>
      <div style={{ display: 'flex', flexDirection: orderType === 'options' && 'column' }}>
        {optionItems}
      </div>
    </React.Fragment>
  );
}

export default Type;
